}

.engagement-card-large:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 40px rgba(0, 0, 0, 0.15);
}

.engagement-card-large img {
    width: 100%;
    height: 180px;
    object-fit: cover;
    object-position: center;
    display: block;
}

.engagement-card-large .engagement-icon {
    font-size: 3rem;
    text-align: center;
    margin: 25px 0 15px;
}

.engagement-card-large h3 {
    font-size: 1.3rem;
    color: var(--primary-blue);
    text-align: center;
    margin-bottom: 10px;
    padding: 0 20px;
}

.engagement-card-large p {
    color: var(--text-light);
    text-align: center;
    padding: 0 20px 25px;
    font-size: 0.95rem;
    line-height: 1.6;
}

.testimonials {
    background: var(--white);
}

.testimonials-slider {
    position: relative;
    max-width: 900px;
    margin: 0 auto;
    padding: 20px 60px;
}

.testimonials-container {
    position: relative;
    overflow: hidden;
}

.testimonial-card {
    display: none;
    background: var(--light-gray);
    border-radius: 15px;
    padding: 40px;
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.1);
}

.testimonial-card.active {
    display: grid;
    grid-template-columns: 200px 1fr;
    gap: 30px;
    align-items: center;
}

.testimonial-image {
    width: 200px;
    height: 200px;
    border-radius: 50%;
    overflow: hidden;
}

.testimonial-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
    display: block;
}

.testimonial-content {
    text-align: left;
}

.stars {
    font-size: 1.5rem;
    margin-bottom: 15px;
    color: var(--gold);
}

.testimonial-content p {
    font-size: 1.1rem;
    font-style: italic;
    color: var(--text-dark);
    margin-bottom: 20px;
    line-height: 1.8;
}

.testimonial-content h4 {
    color: var(--primary-blue);
    font-size: 1.2rem;
    margin-bottom: 5px;
}

.testimonial-content span {
    color: var(--text-light);
    font-size: 0.9rem;
}

.slider-btn {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    background: var(--primary-blue);
    color: var(--white);
    border: none;
    width: 50px;
    height: 50px;
    border-radius: 50%;
    font-size: 2rem;
    cursor: pointer;
    transition: all 0.3s;
    z-index: 10;
}

.slider-btn:hover {
    background: var(--dark-blue);
    transform: translateY(-50%) scale(1.1);
}

.slider-btn.prev {
    left: 0;
}

.slider-btn.next {
    right: 0;
}

.faq-container {
    max-width: 900px;
    margin: 0 auto;
}

.faq-item {
    background: var(--white);
    border-radius: 10px;
    margin-bottom: 15px;
    overflow: hidden;
    box-shadow: 0 3px 10px rgba(0, 0, 0, 0.08);
}

.faq-question {
    width: 100%;
    padding: 20px 25px;
    background: var(--white);
    border: none;
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    font-size: 1.1rem;
    font-weight: 600;
    color: var(--dark-blue);
    transition: all 0.3s;
    text-align: left;
}

.faq-question:hover {
    background: var(--light-gray);
}

.faq-icon {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--primary-blue);
    transition: transform 0.3s;
}

.faq-item.active .faq-icon {
    transform: rotate(45deg);
}

.faq-answer {
    max-height: 0;
    overflow: hidden;
    transition: max-height 0.3s ease, padding 0.3s ease;
    background: var(--white);
}

.faq-item.active .faq-answer {
    max-height: 500px;
    padding: 0 25px 20px;
}

.faq-answer p {
    color: var(--text-light);
    line-height: 1.8;
    font-size: 1rem;
}

footer {
    background: var(--dark-blue);
    color: var(--white);
    padding: 60px 0 20px;
}

.footer-content {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 40px;
    margin-bottom: 40px;
    align-items: flex-start;
}

.footer-section h3 {
    margin-bottom: 20px;
    color: var(--gold);
}

.footer-section ul {
    list-style: none;
}

.footer-section ul li {
    margin-bottom: 10px;
}

.footer-section ul li a {
    color: rgba(255, 255, 255, 0.8);
    text-decoration: none;
    transition: color 0.3s;
}

.footer-section ul li a:hover {
    color: var(--white);
}

.social-links {
    display: flex;
    gap: 15px;
    margin-top: 15px;
}

.social-links a {
    width: 40px;
    height: 40px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s;
}

.social-links a:hover {
    background: var(--gold);
    transform: translateY(-3px);
}

.footer-bottom {
    text-align: center;
    padding-top: 20px;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    color: rgba(255, 255, 255, 0.6);
}

.mobile-menu-toggle {
    display: none;
    background: none;
    border: none;
    font-size: 1.5rem;
    cursor: pointer;
    color: var(--text-dark);
}

@media (max-width: 992px) {
    .products-grid {
        grid-template-columns: repeat(2, 1fr);
    }

    .engagements-grid-six {
        grid-template-columns: repeat(2, 1fr);
    }
}

@media (max-width: 968px) {
    .hero .container {
        grid-template-columns: 1fr;
    }

    .hero-content h1 {
        font-size: 2.5rem;
    }

    .hero-image img {
        max-width: 100%;
        max-height: 300px;
    }

    .about-content {
        grid-template-columns: 1fr;
        gap: 40px;
    }

    .testimonial-card.active {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .testimonial-image {
        margin: 0 auto;
    }

    .testimonial-content {
        text-align: center;
    }

    nav ul {
        display: none;
    }

    .mobile-menu-toggle {
        display: block;
    }

    .dropdown-menu {
        min-width: 100%;
        grid-template-columns: 1fr;
    }
}

@media (max-width: 768px) {
    .hero-content h1 {
        font-size: 2rem;
    }

    .hero-image {
        margin-top: 30px;
    }

    .hero-image img {
        max-height: 250px;
    }

    .section-title {
        font-size: 2rem;
    }

    .hero-buttons {
        flex-direction: column;
    }

    .products-grid {
        grid-template-columns: 1fr;
    }

    .engagements-grid-six {
        grid-template-columns: 1fr;
    }

    .credit-info-cards {
        grid-template-columns: 1fr;
    }

    .testimonials-slider {
        padding: 20px 0;
    }

    .slider-btn {
        width: 40px;
        height: 40px;
        font-size: 1.5rem;
    }
}

.fade-in-up {
    opacity: 0;
    transform: translateY(30px);
    transition: opacity 0.6s ease, transform 0.6s ease;
}

.fade-in-up.visible {
    opacity: 1;
    transform: translateY(0);
}
