    height: 200px;
    border-radius: 50%;
    overflow: hidden;
}

.testimonial-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
    display: block;
}

.testimonial-content {
    text-align: left;
}

.stars {
    font-size: 1.5rem;
    margin-bottom: 15px;
    color: var(--gold);
}

.testimonial-content p {
    font-size: 1.1rem;
    font-style: italic;
    color: var(--text-dark);
    margin-bottom: 20px;
    line-height: 1.8;
}

.testimonial-content h4 {
    color: var(--primary-blue);
    font-size: 1.2rem;
    margin-bottom: 5px;
}

.testimonial-content span {
    color: var(--text-light);
    font-size: 0.9rem;
}

.slider-btn {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    background: var(--primary-blue);
    color: var(--white);
    border: none;
    width: 50px;
    height: 50px;
    border-radius: 50%;
    font-size: 2rem;
    cursor: pointer;
    transition: all 0.3s;
    z-index: 10;
}

.slider-btn:hover {
    background: var(--dark-blue);
    transform: translateY(-50%) scale(1.1);
}

.slider-btn.prev {
    left: 0;
}

.slider-btn.next {
    right: 0;
}

.faq-container {
    max-width: 900px;
    margin: 0 auto;
}

.faq-item {
    background: var(--white);
    border-radius: 10px;
    margin-bottom: 15px;
    overflow: hidden;
    box-shadow: 0 3px 10px rgba(0, 0, 0, 0.08);
}

.faq-question {
    width: 100%;
    padding: 20px 25px;
    background: var(--white);
    border: none;
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    font-size: 1.1rem;
    font-weight: 600;
    color: var(--dark-blue);
    transition: all 0.3s;
    text-align: left;
