    .hero .container {
        grid-template-columns: 1fr;
    }

    .hero-content h1 {
        font-size: 2.5rem;
    }

    .hero-image img {
        max-width: 100%;
        max-height: 300px;
    }

    .products-grid {
        grid-template-columns: 1fr;
    }

    .about-content {
        grid-template-columns: 1fr;
        gap: 40px;
    }

    .engagements-grid-six {
        grid-template-columns: repeat(2, 1fr);
    }

    .testimonial-card.active {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .testimonial-image {
        margin: 0 auto;
    }

    .testimonial-content {
        text-align: center;
    }

    nav ul {
        display: none;
    }

    .mobile-menu-toggle {
        display: block;
    }

    .dropdown-menu {
        min-width: 100%;
        grid-template-columns: 1fr;
    }
}

@media (max-width: 768px) {
    .hero-content h1 {
        font-size: 2rem;
    }

    .hero-image {
        margin-top: 30px;
    }

    .hero-image img {
        max-height: 250px;
    }

    .section-title {
